    };
    const baseConfig = super.getConfig();
    Object.assign(config, baseConfig);
    return config;
  }
}
// A layer handle the random number creation and savemodel behavior.
/** @nocollapse */
BaseRandomLayer.className = 'BaseRandomLayer';
